mapper.update:
UPDATE question
SET subject = #{subject}, content = #{content}, update_date =
#{updateDate}
WHERE id = #{id}

mapper.select:
SELECT q.id, q.parent_id, q.depth, q.subject, q.content, q.create_date, q.update_date, u.id AS author_id, u.username AS author_username, u.email AS author_email FROM question q LEFT JOIN site_user u ON q.author_id = u.id WHERE q.depth = 0
AND (q.subject LIKE CONCAT('%', #{kw}, '%') OR q.content LIKE CONCAT('%', #{kw}, '%'))
ORDER BY q.create_date DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE question
SET content = #{content}, update_date = NOW()
WHERE id = #{id}
